define(["jquery","jquery/ui"],function($){"use strict";$.widget('mage.ordersReturns',{options:{zipCode:'#oar-zip',emailAddress:'#oar-email',searchType:'#quick-search-type-id'},_create:function(){$(this.options.searchType).on('change',$.proxy(this._showIdentifyBlock,this)).trigger('change');},_showIdentifyBlock:function(e){var value=$(e.target).val();$(this.options.zipCode).toggle(value==='zip');$(this.options.emailAddress).toggle(value==='email');}});return $.mage.ordersReturns;});